SELECT CPCOUPON.NAME,LINK ,CPCOUPON.TITLE,LOGO_URL STORELOGO FROM CP_COUPON CPCOUPON, CP_OUT_SITE_COUPON SITECOUPON ,CP_STORE STORE
CPCOUPON.ID=SITECOUPON.COUPON_ID AND CPCOUPON.STORE_ID=STORE.ID
AND SITECOUPON.OUT_SITE_ID = #{outSiteId}
AND CPCOUPON.TITLE LIKE #{name}%
ORDER BY CPCOUPON.ID ASC limit #{beginIndex},#{pageSize}

SELECT CPCOUPON.NAME,LINK ,CPCOUPON.TITLE,LOGO_URL STORELOGO,STORE.ID storeId,CPCOUPON.COUPON_TYPE couponType,CPCOUPON.ID id,CPCOUPON.STORE_URL storeWebSite,SITECOUPON.ID outSiteCouponId FROM CP_COUPON CPCOUPON, CP_OUT_SITE_COUPON SITECOUPON ,CP_STORE STORE
CPCOUPON.ID=SITECOUPON.COUPON_ID AND CPCOUPON.STORE_ID=STORE.ID AND HOT_SORT > 0
AND SITECOUPON.OUT_SITE_ID = #{outSiteId}


