SELECT CPCOUPON.NAME,LINK ,CPCOUPON.TITLE,LOGO_URL STORELOGO FROM
CP_COUPON CPCOUPON, CP_OUT_SITE_COUPON SITECOUPON ,CP_STORE STORE

    CPCOUPON.ID=SITECOUPON.COUPON_ID AND CPCOUPON.STORE_ID=STORE.ID
    AND
    EXPIRE_AT <= NOW()



        AND SITECOUPON.OUT_SITE_ID = #{outSiteId}



        AND STORE.WEBSITE = #{storeUrl}




        AND CPCOUPON.COUPON_TYPE = #{type}

SELECT STORE.TYPE_ID,STORE.TYPE_NAME, CPCOUPON.NAME,LINK ,CPCOUPON.TITLE,LOGO_URL STORELOGO FROM CP_COUPON CPCOUPON, CP_OUT_SITE_COUPON SITECOUPON ,CP_STORE STORE, CP_TYPE_STORE TYPESTORE,CP_SITESTORE_TYPE SITESTORETYPE
CPCOUPON.ID=SITECOUPON.COUPON_ID AND CPCOUPON.STORE_ID=STORE.ID AND TYPESTORE.STORE_ID=STORE.ID AND TYPESTORE.TYPE_ID=SITESTORETYPE.ID
AND SITECOUPON.OUT_SITE_ID = #{outSiteId}
AND SITESTORETYPE.NAME = #{name}

