mapper.delete:
DELETE FROM CP_OUT_SITE_COUPON

	
		OUT_SITE_ID = #{outSiteId}
	
	
		AND STORE_ID = #{storeId}

mapper.update:
update CP_OUT_SITE_COUPON
SET UPDATE_TIME =
#{updateTime,jdbcType=TIMESTAMP},
HOT_SORT =
#{hotSort,jdbcType=INTEGER}
where ID = #{id,jdbcType=INTEGER}

update CP_OUT_SITE_COUPON
SET UPDATE_TIME =
#{updateTime,jdbcType=TIMESTAMP},
ADVISE_SORT =
#{adviseSort,jdbcType=INTEGER}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
select SITECOUPON.ID, COUPON_ID, OUT_SITE_ID, SITECOUPON.STORE_ID, SITECOUPON.TITLE, HOT_SORT, ADVISE_SORT,SITECOUPON.CREATE_USER, SITECOUPON.UPDATE_USER, SITECOUPON.CREATE_TIME, SITECOUPON.UPDATE_TIME,STORE.NAME STORENAME FROM CP_OUT_SITE_COUPON SITECOUPON,CP_STORE STORE
SITECOUPON.STORE_ID=STORE.ID AND HOT_SORT=0 AND OUT_SITE_ID = #{outSiteId}
STORE.NAME LIKE "%"#{name}"%"

select COUNT(*) FROM CP_OUT_SITE_COUPON SITECOUPON,CP_STORE STORE
SITECOUPON.STORE_ID=STORE.ID AND HOT_SORT=0 AND OUT_SITE_ID = #{outSiteId}
STORE.NAME LIKE "%"#{name}"%"
LIMIT #{beginIndex},#{pageSize}

SELECT osp.ID AS mapId, cp.ID as id, cp.NAME AS sourceTile, osp.TITLE AS currentTitle, cp.COUPON_TYPE AS couponType, cp.`CODE` AS code, cp.DES AS description, cp.EXPIRE_AT AS expiryTime, cp.IS_PASS AS isPass, cp.CREATE_TIME AS createTime, osp.CREATE_TIME AS joinTime, cp.IN_TYPE AS inType FROM CP_COUPON cp INNER JOIN CP_OUT_SITE_COUPON osp ON cp.ID = osp.COUPON_ID
osp.OUT_SITE_ID = #{siteId}
AND cp.TITLE LIKE #{search}"%" OR osp.TITLE LIKE #{search}"%"
AND cp.COUPON_TYPE = #{couponType}
AND cp.IS_PASS = #{state}
AND cp.IN_TYPE = #{cteateType}
AND cp.STORE_ID = #{storeId}
ORDER BY id DESC LIMIT #{beginIndex},#{pageSize}

SELECT
count(1)
FROM
CP_COUPON cp
INNER JOIN CP_OUT_SITE_COUPON osp ON
cp.ID =
osp.COUPON_ID

	osp.OUT_SITE_ID = #{siteId}
	
		AND cp.TITLE LIKE #{search}"%" OR osp.TITLE LIKE
		#{search}"%"
	
	
		AND cp.COUPON_TYPE = #{couponType}
	
	
		AND cp.IS_PASS = #{state}
	
	
		AND cp.IN_TYPE = #{cteateType}
	
	
		AND cp.STORE_ID = #{storeId}

SELECT osp.ID AS id, osp.TITLE AS title, cp.COUPON_TYPE AS couponType, cp.`CODE` AS code, cp.DES AS description, cp.LINK AS link, s.LOGO_URL AS storeLogo, s.NAME AS storeName, s.WEBSITE AS storeWbsite, s.ID AS storeId FROM CP_COUPON cp INNER JOIN CP_OUT_SITE_COUPON osp ON cp.ID = osp.COUPON_ID INNER JOIN CP_STORE s ON s.ID = osp.STORE_ID
osp.OUT_SITE_ID = #{siteId} AND osp.STORE_ID IN
#{storeId}
AND cp.COUPON_TYPE = #{couponType}
ORDER BY ID DESC LIMIT #{beginIndex},#{pageSize}

SELECT COUNT(1) FROM CP_COUPON cp INNER JOIN CP_OUT_SITE_COUPON osp ON cp.ID = osp.COUPON_ID INNER JOIN CP_STORE s ON s.ID = osp.STORE_ID
osp.OUT_SITE_ID = #{siteId} AND osp.STORE_ID IN
#{storeId}
AND cp.COUPON_TYPE = #{couponType}
