mapper.update:
		update CP_STORE
		LOGO_URL = #{logoUrl,jdbcType=VARCHAR},
		UPDATE_USER =#{updateUser,jdbcType=INTEGER},
		UPDATE_TIME =#{updateTime,jdbcType=TIMESTAMP}

		where ID = #{id,jdbcType=INTEGER}

mapper.select:
SELECT

FROM CP_STORE

	
		NAME LIKE "%"#{search}"%" OR WEBSITE LIKE "%"#{search}"%"
	
	
		AND ID IN
		
			#{id}
		
	
	
		AND ID NOT IN
		
			#{id}
		
	
	
		AND COUNTRY = #{country}
	
	
		AND TYPE_ID != 0
	
	
		AND TYPE_ID IS NULL
	
	
		AND APPROVAL = #{approval}
	
	
		AND IS_COMPLETE = #{isComplete}
	

ORDER BY ID ASC
limit #{beginIndex},#{pageSize}

SELECT COUNT(1) FROM CP_STORE

	
		NAME LIKE "%"#{search}"%" OR WEBSITE LIKE "%"#{search}"%"
	
	
		AND ID IN
		
			#{id}
		
	
	
		AND ID NOT IN
		
			#{id}
		
	
	
		AND COUNTRY = #{country}
	
	
		AND TYPE_ID != 0
	
	
		AND TYPE_ID IS NULL
	
	
		AND APPROVAL = #{approval}
	
	
		AND IS_COMPLETE = #{isComplete}

		 SELECT *  FROM (
		  SELECT STORE.ID id ,NAME,WEBSITE website,LOGO_URL logo,TYPE_ID typeId,TYPE_NAME typeName ,COUNTRY country,(SELECT COUNT(*)   FROM  CP_COUPON COU WHERE COU.STORE_ID=STORE.ID)toalCount,
 (SELECT COUNT(*)   FROM  CP_COUPON  WHERE STORE_ID=STORE.ID  AND  EXPIRE_AT >=NOW()) validCount FROM CP_STORE STORE ,CP_SCRAPY_STORE SCSTORE 
	
	STORE.ID=SCSTORE.STORE_ID 
	AND IN_TYPE=0
			
				NAME LIKE "%"#{search}"%"  OR WEBSITE LIKE "%"#{search}"%"
			
			
				AND COUNTRY like "%"#{country}"%"
			
			
			
			
			
			
				AND TYPE_NAME = #{typeName}
			
			
			
				AND SCSTORE.SCRAPY_ID = #{scrapyId}
			
		
		
		 )temp
		 
			
				temp.VALIDCOUNT>=#{validCount}
			
		
		
		ORDER BY ID ASC
		limit #{beginIndex},#{pageSize}

