SELECT STORE.NAME name , STORE.LOGO_URL logoUrl, STORE.WEBSITE webSite , STORE.ID AS storeId, SITESTORE.SHOW_NAME showName FROM CP_STORE STORE,CP_OUT_SITE_STORE SITESTORE
STORE.ID=SITESTORE.STORE_ID
AND OUT_ID=#{siteId}
AND STORE.NAME LIKE "%"#{name}"%"
ORDER BY SITESTORE.ID ASC limit #{beginIndex},#{pageSize}

SELECT STORE.NAME name , STORE.LOGO_URL logoUrl,STORE.WEBSITE webSite ,SITESTORE.SHOW_NAME showName FROM CP_STORE STORE,CP_OUT_SITE_STORE SITESTORE
STORE.ID=SITESTORE.STORE_ID AND HOT_SORT <> 0
AND OUT_ID=#{siteId}
