mapper.select:
SELECT COUNT(1) FROM CP_COUPON

    EXPIRE_AT >= NOW()
    AND CREATE_TIME <= #{endDate}

        AND SCRAPY =#{spider}

SELECT COUNT(1) FROM CP_COUPON

    AND CREATE_TIME <= #{endDate}

        AND SCRAPY =#{spider}

SELECT COUNT(1) FROM CP_COUPON

    AND CREATE_TIME >= #{beginDate}
    AND CREATE_TIME <= #{endDate}

        AND SCRAPY =#{spider}

SELECT COUNT( DISTINCT STORE_ID)
FROM CP_COUPON

    AND CREATE_TIME >= #{beginDate}
    AND CREATE_TIME <= #{endDate}

        AND SCRAPY =#{spider}

SELECT COUNT(1) FROM
CP_STORE store
INNER JOIN
CP_SCRAPY_STORE C_S_S
ON
store.ID = C_S_S.STORE_ID


        AND C_S_S.SCRAPY_ID = #{spiderId}

    AND store.CREATE_TIME >= #{beginDate}
    AND store.CREATE_TIME <= #{endDate}

SELECT COUNT(1) FROM CP_OUT_SITE_COUPON COSC
INNER JOIN CP_COUPON CC
ON COSC.COUPON_ID = CC.ID

    CC.EXPIRE_AT >= NOW()
    AND COSC.CREATE_TIME <= #{endDate}
    AND COSC.OUT_SITE_ID =#{siteId}

SELECT COUNT(1) FROM CP_OUT_SITE_COUPON

    CREATE_TIME <= #{endDate}
    AND OUT_SITE_ID =#{siteId}

SELECT COUNT(1) FROM CP_OUT_SITE_COUPON

    CREATE_TIME >= #{beginDate}
    AND CREATE_TIME <= #{endDate}
    AND OUT_SITE_ID =#{siteId}

SELECT COUNT( DISTINCT STORE_ID)
FROM CP_OUT_SITE_COUPON

    CREATE_TIME >= #{beginDate}
    AND CREATE_TIME <= #{endDate}
    AND OUT_SITE_ID =#{siteId}

SELECT COUNT(1) FROM
CP_OUT_SITE_STORE store

    OUT_ID =#{siteId}
    AND store.CREATE_TIME >= #{beginDate}
    AND store.CREATE_TIME <= #{endDate}
